<div id="tozpom-popup-box-preview-{{num}}" class="popupally-pro-outer-preview-tozpom-{{num}}">
	<div class="popupally-pro-inner-pro-tozpom-{{num}}">
		<div style="clear:both;height:0.0001px"></div>
		<div popupally-preview-linked="tozpom-headline-{{num}}" class="desc-pro-tozpom tozpom-preview-headline-{{num}}" id="tozpom-preview-headline-{{num}}"></div>
		<div popupally-preview-linked="tozpom-logo-img-{{num}}" class="logo-img-pro-tozpom tozpom-preview-img-{{num}}" id="tozpom-preview-img-{{num}}"></div>
		<div class="content-pro-tozpom">
			<input popupally-preview-linked="tozpom-input-name-{{num}}" type="text" id="tozpom-preview-name-{{num}}" name="name" {{tozpom-preview-hide-name}} class="field-pro-tozpom-name tozpom-preview-name-{{num}}" placeholder="{{tozpom-name-placeholder}}"/>
			<input popupally-preview-linked="tozpom-input-email-{{num}}" type="email" id="tozpom-preview-email-{{num}}" name="email" class="field-pro-tozpom-email tozpom-preview-email-{{num}}" placeholder="{{tozpom-email-placeholder}}"/>
			<input popupally-preview-linked="tozpom-subscribe-button-{{num}}" type="button" id="tozpom-preview-subscribe-button-{{num}}" class="submit-pro-tozpom no-click-through tozpom-preview-subscribe-button-{{num}}" value="{{tozpom-subscribe-button-text}}"/>
		</div>
		<div popupally-preview-linked="tozpom-privacy-text-{{num}}" class="privacy-pro-tozpom tozpom-preview-privacy-text-{{num}}" id="tozpom-preview-privacy-text-{{num}}"></div>
	</div>
</div>