<div id="popup-box-preview-{{num}}" class="popupally-pro-outer-preview-gfcr-{{num}} popupally-pro-outer-preview-background-bxsjbi-{{num}}">
	<div class="popupally-pro-inner-pro-gfcr-{{num}}">
		<div class="popupally-pro-center-pro-gfcr">
			<div popupally-preview-linked="bxsjbi-headline-{{num}}" class="desc-pro-gfcr customizable-color-text-{{num}} preview-headline-{{num}}" id="preview-headline-{{num}}"></div>
			<div class="logo-row-pro-gfcr logo-row-{{num}}" id="logo-row-{{num}}">
				<div class="clear-pro-gfcr"></div>
				<img popupally-preview-linked="bxsjbi-logo-img-{{num}}" class="logo-img-pro-gfcr preview-img-{{num}}" id="preview-img-{{num}}" src="{{image-url}}" alt="">
				<div popupally-preview-linked="bxsjbi-sales-text-{{num}}" class="logo-text-pro-gfcr customizable-color-text-{{num}} preview-sales-text-{{num}}" id="preview-sales-text-{{num}}"></div>
				<div class="clear-pro-gfcr"></div>
			</div>
			<div class="content-pro-gfcr">
				<input popupally-preview-linked="bxsjbi-name-input-{{num}}" type="text" id="preview-name-{{num}}" name="name" {{bxsjbi-preview-hide-name}} class="field-pro-gfcr preview-input-desktop-{{num}} preview-input-{{num}} preview-name-{{num}}" placeholder="{{name-placeholder}}"/>
				<input popupally-preview-linked="bxsjbi-email-input-{{num}}" type="email" id="preview-email-{{num}}" name="email" class="field-pro-gfcr preview-input-desktop-{{num}} preview-input-{{num}} preview-email-{{num}}" placeholder="{{email-placeholder}}"/>
				<input popupally-preview-linked="bxsjbi-subscribe-button-{{num}}" type="button" id="subscribe-button-{{num}}" class="submit-pro-gfcr no-click-through subscribe-button-{{num}}" value="{{subscribe-button-text}}"/>
			</div>
			<div popupally-preview-linked="bxsjbi-privacy-text-{{num}}" class="privacy-pro-gfcr customizable-color-text-{{num}} privacy-text-{{num}}" id="privacy-text-{{num}}"></div>
		</div>
	</div>
</div>