<div id="khwybd-popup-box-640-preview-{{num}}" class="popupally-pro-outer-640-preview-khwybd-{{num}} popupally-pro-outer-preview-background-khwybd-{{num}}">
	<div class="popupally-pro-inner-khwybd-{{num}}">
		<div popupally-preview-linked="khwybd-config-image-1-640-{{num}}" class="image-1-pro-khwybd khwybd-preview-image-1-{{num}}" id="khwybd-preview-image-1-640-{{num}}"></div>
		<div popupally-preview-linked="khwybd-config-image-2-640-{{num}}" class="image-2-pro-khwybd khwybd-preview-image-2-{{num}}" id="khwybd-preview-image-2-640-{{num}}"></div>
		<div popupally-preview-linked="khwybd-config-headline-640-{{num}}" class="desc-pro-khwybd khwybd-preview-headline-{{num}}" id="khwybd-preview-headline-640-{{num}}"></div>
		<div popupally-preview-linked="khwybd-config-textbox-1-640-{{num}}" class="textbox-1-pro-khwybd khwybd-preview-textbox-1-{{num}}" id="khwybd-preview-textbox-1-640-{{num}}"></div>
		<div popupally-preview-linked="khwybd-config-textbox-2-640-{{num}}" class="textbox-2-pro-khwybd khwybd-preview-textbox-2-{{num}}" id="khwybd-preview-textbox-2-640-{{num}}"></div>
		<input popupally-preview-linked="khwybd-config-name-input-640-{{num}}" type="text" class="field-pro-khwybd-name khwybd-preview-name-{{num}}" {{khwybd-preview-hide-name}} id="khwybd-preview-name-640-{{num}}" name="name" placeholder="{{khwybd-name-placeholder}}"/>
		<input popupally-preview-linked="khwybd-config-lname-input-640-{{num}}" type="text" class="field-pro-khwybd-lname khwybd-preview-lname-{{num}}" {{khwybd-preview-hide-lname}} id="khwybd-preview-lname-640-{{num}}" name="lname" placeholder="{{khwybd-lname-placeholder}}"/>
		<input popupally-preview-linked="khwybd-config-email-input-640-{{num}}" type="email" class="field-pro-khwybd-email khwybd-preview-email-{{num}}" id="khwybd-preview-email-640-{{num}}" name="email" placeholder="{{khwybd-email-placeholder}}"/>
		<input popupally-preview-linked="khwybd-config-subscribe-button-640-{{num}}" type="button" class="submit-pro-khwybd no-click-through khwybd-subscribe-button-{{num}}" id="khwybd-subscribe-button-640-{{num}}" value="{{khwybd-subscribe-button-text}}"/>
	</div>
</div>