<div id="khwybd-popup-box-preview-{{num}}" class="popupally-pro-outer-preview-khwybd-{{num}} popupally-pro-outer-preview-background-khwybd-{{num}}">
	<div class="popupally-pro-inner-khwybd-{{num}}">
		<div popupally-preview-linked="khwybd-config-image-1-{{num}}" class="image-1-pro-khwybd khwybd-preview-image-1-{{num}}" id="khwybd-preview-image-1-{{num}}"></div>
		<div popupally-preview-linked="khwybd-config-image-2-{{num}}" class="image-2-pro-khwybd khwybd-preview-image-2-{{num}}" id="khwybd-preview-image-2-{{num}}"></div>
		<div popupally-preview-linked="khwybd-config-headline-{{num}}" class="desc-pro-khwybd khwybd-preview-headline-{{num}}" id="khwybd-preview-headline-{{num}}"></div>
		<div popupally-preview-linked="khwybd-config-textbox-1-{{num}}" class="textbox-1-pro-khwybd khwybd-preview-textbox-1-{{num}}" id="khwybd-preview-textbox-1-{{num}}"></div>
		<div popupally-preview-linked="khwybd-config-textbox-2-{{num}}" class="textbox-2-pro-khwybd khwybd-preview-textbox-2-{{num}}" id="khwybd-preview-textbox-2-{{num}}"></div>
		<input popupally-preview-linked="khwybd-config-name-input-{{num}}" type="text" id="khwybd-preview-name-{{num}}" name="name" {{khwybd-preview-hide-name}} class="field-pro-khwybd-name khwybd-preview-name-{{num}}" placeholder="{{khwybd-name-placeholder}}"/>
		<input popupally-preview-linked="khwybd-config-lname-input-{{num}}" type="text" id="khwybd-preview-lname-{{num}}" name="lname" {{khwybd-preview-hide-lname}} class="field-pro-khwybd-lname khwybd-preview-lname-{{num}}" placeholder="{{khwybd-lname-placeholder}}"/>
		<input popupally-preview-linked="khwybd-config-email-input-{{num}}" type="email" id="khwybd-preview-email-{{num}}" name="email" class="field-pro-khwybd-email khwybd-preview-email-{{num}}" placeholder="{{khwybd-email-placeholder}}"/>
		<input popupally-preview-linked="khwybd-config-subscribe-button-{{num}}" type="button" id="khwybd-subscribe-button-{{num}}" class="submit-pro-khwybd no-click-through khwybd-subscribe-button-{{num}}" value="{{khwybd-subscribe-button-text}}"/>
	</div>
</div>