<td id="popupally-fluid-responsive-header-{{id}}-{{uid}}-{{responsive-id}}" class="popupally-style-responsive-tab-label-col {{is-active}}" click-target="#popupally-style-selected-responsive-{{id}}-{{uid}}" click-value="{{responsive-id}}"
	tab-group="popupally-responsive-tab-group-{{id}}-{{uid}}" target="{{responsive-id}}" active-class="popupally-style-responsive-tab-active">
	<input type="hidden" popupally-change-source="edit-name-responsive-{{id}}-{{uid}}-{{responsive-id}}"
		   id="edit-name-responsive-{{id}}-{{uid}}-{{responsive-id}}" value="display" />
	<div  hide-toggle data-dependency="edit-name-responsive-{{id}}-{{uid}}-{{responsive-id}}" data-dependency-value="display"
		  name-sync-text="responsive-{{id}}-{{uid}}-{{responsive-id}}"
		  click-value="edit" double-click-target="#edit-name-responsive-{{id}}-{{uid}}-{{responsive-id}}">{{label}}</div>
	<input class="popupally-name-edit full-width" name-sync-master="responsive-{{id}}-{{uid}}-{{responsive-id}}" style="display:none;"
		   hide-toggle data-dependency="edit-name-responsive-{{id}}-{{uid}}-{{responsive-id}}" data-dependency-value="edit"
		   name="[{{id}}][{{uid}}][responsive][{{responsive-id}}][label]" value="{{label}}" />
</td>