<div id="vtgjid-popup-box-640-preview-{{num}}" class="popupally-pro-outer-640-preview-vtgjid-{{num}} popupally-pro-outer-preview-background-vtgjid-{{num}}">
	<div class="popupally-pro-inner-vtgjid-{{num}}">
		<div popupally-preview-linked="vtgjid-config-image-1-640-{{num}}" class="image-1-pro-vtgjid vtgjid-preview-image-1-{{num}}" id="vtgjid-preview-image-1-640-{{num}}"></div>
		<div popupally-preview-linked="vtgjid-config-image-2-640-{{num}}" class="image-2-pro-vtgjid vtgjid-preview-image-2-{{num}}" id="vtgjid-preview-image-2-640-{{num}}"></div>
		<div popupally-preview-linked="vtgjid-config-headline-640-{{num}}" class="desc-pro-vtgjid vtgjid-preview-headline-{{num}}" id="vtgjid-preview-headline-640-{{num}}"></div>
		<div popupally-preview-linked="vtgjid-config-textbox-1-640-{{num}}" class="textbox-1-pro-vtgjid vtgjid-preview-textbox-1-{{num}}" id="vtgjid-preview-textbox-1-640-{{num}}"></div>
		<div popupally-preview-linked="vtgjid-config-textbox-2-640-{{num}}" class="textbox-2-pro-vtgjid vtgjid-preview-textbox-2-{{num}}" id="vtgjid-preview-textbox-2-640-{{num}}"></div>
		<input popupally-preview-linked="vtgjid-config-name-input-640-{{num}}" type="text" class="field-pro-vtgjid-name vtgjid-preview-name-{{num}}" {{vtgjid-preview-hide-name}} id="vtgjid-preview-name-640-{{num}}" name="name" placeholder="{{vtgjid-name-placeholder}}"/>
		<input popupally-preview-linked="vtgjid-config-lname-input-640-{{num}}" type="text" class="field-pro-vtgjid-lname vtgjid-preview-lname-{{num}}" {{vtgjid-preview-hide-lname}} id="vtgjid-preview-lname-640-{{num}}" name="lname" placeholder="{{vtgjid-lname-placeholder}}"/>
		<input popupally-preview-linked="vtgjid-config-email-input-640-{{num}}" type="email" class="field-pro-vtgjid-email vtgjid-preview-email-{{num}}" id="vtgjid-preview-email-640-{{num}}" name="email" placeholder="{{vtgjid-email-placeholder}}"/>
		<input popupally-preview-linked="vtgjid-config-subscribe-button-640-{{num}}" type="button" class="submit-pro-vtgjid no-click-through vtgjid-subscribe-button-{{num}}" id="vtgjid-subscribe-button-640-{{num}}" value="{{vtgjid-subscribe-button-text}}"/>
