<div id="vtgjid-popup-box-960-preview-{{num}}" class="popupally-pro-outer-960-preview-vtgjid-{{num}} popupally-pro-outer-preview-background-vtgjid-{{num}}">
	<div class="popupally-pro-inner-vtgjid-{{num}}">
		<div popupally-preview-linked="vtgjid-config-image-1-960-{{num}}" class="image-1-pro-vtgjid vtgjid-preview-image-1-{{num}}" id="vtgjid-preview-image-1-960-{{num}}"></div>
		<div popupally-preview-linked="vtgjid-config-image-2-960-{{num}}" class="image-2-pro-vtgjid vtgjid-preview-image-2-{{num}}" id="vtgjid-preview-image-2-960-{{num}}"></div>
		<div popupally-preview-linked="vtgjid-config-headline-960-{{num}}" class="desc-pro-vtgjid vtgjid-preview-headline-{{num}}" id="vtgjid-preview-headline-960-{{num}}"></div>
		<div popupally-preview-linked="vtgjid-config-textbox-1-960-{{num}}" class="textbox-1-pro-vtgjid vtgjid-preview-textbox-1-{{num}}" id="vtgjid-preview-textbox-1-960-{{num}}"></div>
		<div popupally-preview-linked="vtgjid-config-textbox-2-960-{{num}}" class="textbox-2-pro-vtgjid vtgjid-preview-textbox-2-{{num}}" id="vtgjid-preview-textbox-2-960-{{num}}"></div>
		<input popupally-preview-linked="vtgjid-config-name-input-960-{{num}}" type="text" class="field-pro-vtgjid-name vtgjid-preview-name-{{num}}" {{vtgjid-preview-hide-name}} id="vtgjid-preview-name-960-{{num}}" name="name" placeholder="{{vtgjid-name-placeholder}}"/>
		<input popupally-preview-linked="vtgjid-config-lname-input-960-{{num}}" type="text" class="field-pro-vtgjid-lname vtgjid-preview-lname-{{num}}" {{vtgjid-preview-hide-lname}} id="vtgjid-preview-lname-960-{{num}}" name="lname" placeholder="{{vtgjid-lname-placeholder}}"/>
		<input popupally-preview-linked="vtgjid-config-email-input-960-{{num}}" type="email" class="field-pro-vtgjid-email vtgjid-preview-email-{{num}}" id="vtgjid-preview-email-960-{{num}}" name="email" placeholder="{{vtgjid-email-placeholder}}"/>
		<input popupally-preview-linked="vtgjid-config-subscribe-button-960-{{num}}" type="button" class="submit-pro-vtgjid no-click-through vtgjid-subscribe-button-{{num}}" id="vtgjid-subscribe-button-960-{{num}}" value="{{vtgjid-subscribe-button-text}}"/>
