<div id="vtgjid-popup-box-preview-{{num}}" class="popupally-pro-outer-preview-vtgjid-{{num}} popupally-pro-outer-preview-background-vtgjid-{{num}}">
	<div class="popupally-pro-inner-vtgjid-{{num}}">
		<div popupally-preview-linked="vtgjid-config-image-1-{{num}}" class="image-1-pro-vtgjid vtgjid-preview-image-1-{{num}}" id="vtgjid-preview-image-1-{{num}}"></div>
		<div popupally-preview-linked="vtgjid-config-image-2-{{num}}" class="image-2-pro-vtgjid vtgjid-preview-image-2-{{num}}" id="vtgjid-preview-image-2-{{num}}"></div>
		<div popupally-preview-linked="vtgjid-config-headline-{{num}}" class="desc-pro-vtgjid vtgjid-preview-headline-{{num}}" id="vtgjid-preview-headline-{{num}}">{{vtgjid-headline}}</div>
		<div popupally-preview-linked="vtgjid-config-textbox-1-{{num}}" class="textbox-1-pro-vtgjid vtgjid-preview-textbox-1-{{num}}" id="vtgjid-preview-textbox-1-{{num}}">{{vtgjid-textbox-1}}</div>
		<div popupally-preview-linked="vtgjid-config-textbox-2-{{num}}" class="textbox-2-pro-vtgjid vtgjid-preview-textbox-2-{{num}}" id="vtgjid-preview-textbox-2-{{num}}">{{vtgjid-textbox-2}}</div>
		<input popupally-preview-linked="vtgjid-config-name-input-{{num}}" type="text" id="vtgjid-preview-name-{{num}}" name="name" {{vtgjid-preview-hide-name}} class="field-pro-vtgjid-name vtgjid-preview-name-{{num}}" placeholder="{{vtgjid-name-placeholder}}"/>
		<input popupally-preview-linked="vtgjid-config-lname-input-{{num}}" type="text" id="vtgjid-preview-lname-{{num}}" name="lname" {{vtgjid-preview-hide-lname}} class="field-pro-vtgjid-lname vtgjid-preview-lname-{{num}}" placeholder="{{vtgjid-lname-placeholder}}"/>
		<input popupally-preview-linked="vtgjid-config-email-input-{{num}}" type="email" id="vtgjid-preview-email-{{num}}" name="email" class="field-pro-vtgjid-email vtgjid-preview-email-{{num}}" placeholder="{{vtgjid-email-placeholder}}"/>
		<input popupally-preview-linked="vtgjid-config-subscribe-button-{{num}}" type="button" id="vtgjid-subscribe-button-{{num}}" class="submit-pro-vtgjid no-click-through vtgjid-subscribe-button-{{num}}" value="{{vtgjid-subscribe-button-text}}"/>
	</div>
</div>