<div id="cjthhv-popup-box-preview-{{num}}" class="popupally-pro-outer-preview-cjthhv-{{num}} popupally-pro-outer-preview-background-cjthhv-{{num}}">
	<div class="popupally-pro-inner-cjthhv-{{num}}" id="cjthhv-popup-content-box-preview-{{num}}">
		<div class="popupally-pro-center-cjthhv">
			<div popupally-preview-linked="cjthhv-config-headline-{{num}}" class="desc-pro-cjthhv cjthhv-customizable-color-text-{{num}} cjthhv-preview-headline-{{num}}" id="cjthhv-preview-headline-{{num}}"></div>
			<div class="content-pro-cjthhv">
				<input popupally-preview-linked="cjthhv-config-name-input-{{num}}" type="text" id="cjthhv-preview-name-{{num}}" name="name" {{cjthhv-preview-hide-name}} class="field-pro-cjthhv-name cjthhv-preview-name-{{num}}" placeholder="{{cjthhv-name-placeholder}}"/>
				<input popupally-preview-linked="cjthhv-config-email-input-{{num}}" type="email" id="cjthhv-preview-email-{{num}}" name="email" class="field-pro-cjthhv-email cjthhv-preview-email-{{num}}" placeholder="{{cjthhv-email-placeholder}}"/>
				<input popupally-preview-linked="cjthhv-config-subscribe-button-{{num}}" type="button" id="cjthhv-subscribe-button-{{num}}" class="submit-pro-cjthhv no-click-through cjthhv-subscribe-button-{{num}}" value="{{cjthhv-subscribe-button-text}}"/>
			</div>
		</div>
	</div>
</div>