<div id="zewges-popup-box-960-preview-{{num}}" class="popupally-pro-outer-960-preview-zewges-{{num}} popupally-pro-outer-preview-background-zewges-{{num}}">
	<div class="popupally-pro-inner-zewges-{{num}}">
		<div popupally-preview-linked="zewges-config-image-1-960-{{num}}" class="image-1-pro-zewges zewges-preview-image-1-{{num}}" id="zewges-preview-image-1-960-{{num}}"></div>
		<div popupally-preview-linked="zewges-config-video-1-960-{{num}}" class="video-1-pro-zewges zewges-preview-video-1-{{num}}" id="zewges-preview-video-1-960-{{num}}"></div>
		<div popupally-preview-linked="zewges-config-headline-960-{{num}}" class="desc-pro-zewges zewges-preview-headline-{{num}}" id="zewges-preview-headline-960-{{num}}"></div>
		<div popupally-preview-linked="zewges-config-textbox-1-960-{{num}}" class="textbox-1-pro-zewges zewges-preview-textbox-1-{{num}}" id="zewges-preview-textbox-1-960-{{num}}"></div>
		<div popupally-preview-linked="zewges-config-textbox-2-960-{{num}}" class="textbox-2-pro-zewges zewges-preview-textbox-2-{{num}}" id="zewges-preview-textbox-2-960-{{num}}"></div>
		<input popupally-preview-linked="zewges-config-name-input-960-{{num}}" type="text" class="field-pro-zewges-name zewges-preview-name-{{num}}" {{zewges-preview-hide-name}} id="zewges-preview-name-960-{{num}}" name="name" placeholder="{{zewges-name-placeholder}}"/>
		<input popupally-preview-linked="zewges-config-lname-input-960-{{num}}" type="text" class="field-pro-zewges-lname zewges-preview-lname-{{num}}" {{zewges-preview-hide-lname}} id="zewges-preview-lname-960-{{num}}" name="lname" placeholder="{{zewges-lname-placeholder}}"/>
		<input popupally-preview-linked="zewges-config-email-input-960-{{num}}" type="email" class="field-pro-zewges-email zewges-preview-email-{{num}}" id="zewges-preview-email-960-{{num}}" name="email" placeholder="{{zewges-email-placeholder}}"/>
		<input popupally-preview-linked="zewges-config-subscribe-button-960-{{num}}" type="button" class="submit-pro-zewges no-click-through zewges-subscribe-button-{{num}}" id="zewges-subscribe-button-960-{{num}}" value="{{zewges-subscribe-button-text}}"/>
	</div>
</div>