<div id="zewges-popup-box-preview-{{num}}" class="popupally-pro-outer-preview-zewges-{{num}} popupally-pro-outer-preview-background-zewges-{{num}}">
	<div class="popupally-pro-inner-zewges-{{num}}">
		<div popupally-preview-linked="zewges-config-image-1-{{num}}" class="image-1-pro-zewges zewges-preview-image-1-{{num}}" id="zewges-preview-image-1-{{num}}"></div>
		<div popupally-preview-linked="zewges-config-video-1-{{num}}" class="video-1-pro-zewges zewges-preview-video-1-{{num}}" id="zewges-preview-video-1-{{num}}"></div>
		<div popupally-preview-linked="zewges-config-headline-{{num}}" class="desc-pro-zewges zewges-preview-headline-{{num}}" id="zewges-preview-headline-{{num}}"></div>
		<div popupally-preview-linked="zewges-config-textbox-1-{{num}}" class="textbox-1-pro-zewges zewges-preview-textbox-1-{{num}}" id="zewges-preview-textbox-1-{{num}}"></div>
		<div popupally-preview-linked="zewges-config-textbox-2-{{num}}" class="textbox-2-pro-zewges zewges-preview-textbox-2-{{num}}" id="zewges-preview-textbox-2-{{num}}"></div>
		<input popupally-preview-linked="zewges-config-name-input-{{num}}" type="text" id="zewges-preview-name-{{num}}" name="name" {{zewges-preview-hide-name}} class="field-pro-zewges-name zewges-preview-name-{{num}}" placeholder="{{zewges-name-placeholder}}"/>
		<input popupally-preview-linked="zewges-config-lname-input-{{num}}" type="text" id="zewges-preview-lname-{{num}}" name="lname" {{zewges-preview-hide-lname}} class="field-pro-zewges-lname zewges-preview-lname-{{num}}" placeholder="{{zewges-lname-placeholder}}"/>
		<input popupally-preview-linked="zewges-config-email-input-{{num}}" type="email" id="zewges-preview-email-{{num}}" name="email" class="field-pro-zewges-email zewges-preview-email-{{num}}" placeholder="{{zewges-email-placeholder}}"/>
		<input popupally-preview-linked="zewges-config-subscribe-button-{{num}}" type="button" id="zewges-subscribe-button-{{num}}" class="submit-pro-zewges no-click-through zewges-subscribe-button-{{num}}" value="{{zewges-subscribe-button-text}}"/>
	</div>
</div>